import React, { useEffect, useState, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { MapPin, Settings, Plus } from 'lucide-react';
import { useScrollEatsStore } from './store';
import { Restaurant, UserRating } from './types';
import RestaurantCard from './components/RestaurantCard';
import InfiniteScroll from './components/InfiniteScroll';
import BottomNavigation from './components/BottomNavigation';
import CreateReview from './components/CreateReview';
import SearchScreen from './components/SearchScreen';
import ProfileScreen from './components/ProfileScreen';
import LoadingSpinner from './components/LoadingSpinner';
import { apiService, testGooglePlacesAPI } from './services/api';

function App() {
  const {
    currentLocation,
    restaurants,
    userRatings,
    favorites,
    isLoading,
    error,
    currentIndex,
    hasMore,
    setCurrentLocation,
    addRestaurants,
    clearRestaurants,
    addUserRating,
    toggleFavorite,
    setLoading,
    setError,
    setHasMore,
    setCurrentIndex,
  } = useScrollEatsStore();

  const [showSettings, setShowSettings] = useState(false);
  const [activeTab, setActiveTab] = useState('home');
  const [showCreateReview, setShowCreateReview] = useState(false);
  const [locationStatus, setLocationStatus] = useState<'loading' | 'success' | 'error' | 'manual'>('loading');
  const [showLocationInput, setShowLocationInput] = useState(false);
  const [manualLocation, setManualLocation] = useState({ latitude: '', longitude: '' });

  const loadRestaurants = useCallback(async () => {
    // Prevent multiple simultaneous calls
    if (!currentLocation || isLoading || restaurants.length > 0) {
      console.log('Skipping restaurant load:', { 
        hasLocation: !!currentLocation, 
        isLoading,
        restaurantCount: restaurants.length 
      });
      return;
    }

    console.log('🔄 Starting to load restaurants...');
    setLoading(true);
    setError(null);

    try {
      console.log('📍 Loading restaurants from Google Places API...');
      console.log('📍 Current location:', currentLocation);

      const realRestaurants = await apiService.getNearbyRestaurants(
        currentLocation.latitude, 
        currentLocation.longitude,
        10000 // 10km radius for more restaurants
      );

      console.log(`✅ Loaded ${realRestaurants.length} restaurants from API`);

      if (realRestaurants.length === 0) {
        setError('No restaurants found nearby. Try expanding your search radius.');
        setHasMore(false);
        return;
      }

      // Clear any existing restaurants and add new ones
      clearRestaurants();
      addRestaurants(realRestaurants);

      // Set hasMore to false since we're loading all restaurants at once
      setHasMore(false);

      console.log(`🎉 Total restaurants in store: ${realRestaurants.length}`);
    } catch (err) {
      console.error('❌ Error loading restaurants:', err);

      let errorMessage = 'Failed to load restaurants. Please try again.';

      if (err instanceof Error) {
        if (err.message.includes('API key')) {
          errorMessage = 'API configuration error. Please check your Google Places API key.';
        } else if (err.message.includes('network')) {
          errorMessage = 'Network error. Please check your internet connection.';
        } else if (err.message.includes('timeout')) {
          errorMessage = 'Request timed out. Please try again.';
        } else {
          errorMessage = err.message;
        }
      }

      setError(errorMessage);
      setHasMore(false);
    } finally {
      setLoading(false);
    }
  }, [currentLocation, isLoading, restaurants.length, addRestaurants, clearRestaurants, setLoading, setError, setHasMore]);

  const refreshLocation = useCallback(() => {
    setLocationStatus('loading');

    // Clear existing restaurants to force reload
    clearRestaurants();

    if (navigator.geolocation) {
      const options = {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 0 // Force fresh location
      };

      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          console.log('✅ Location refreshed:', { latitude, longitude });
          setCurrentLocation({ latitude, longitude });
          setLocationStatus('success');
        },
        (error) => {
          console.error('❌ Error refreshing location:', error);
          setLocationStatus('error');
          setError('Failed to get your location. Using default location.');
          setTimeout(() => setError(null), 3000);
        },
        options
      );
    }
  }, [setCurrentLocation, setError, clearRestaurants]);

  // Get user's location on app start
  useEffect(() => {
    // Test API first
    const testAPI = async () => {
      console.log('🔍 Testing Google Places API...');
      const isWorking = await testGooglePlacesAPI();
      if (!isWorking) {
        console.warn('⚠️ Google Places API test failed - will use fallback data');
      }
    };

    testAPI();

    if (navigator.geolocation) {
      console.log('Requesting user location...');

      const options = {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 300000 // 5 minutes
      };

      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          console.log('✅ Location obtained:', { latitude, longitude });
          setCurrentLocation({ latitude, longitude });
          setLocationStatus('success');
        },
        (error) => {
          console.error('❌ Error getting location:', error);

          // Provide specific error messages
          let errorMessage = 'Unable to get your location.';
          switch (error.code) {
            case error.PERMISSION_DENIED:
              errorMessage = 'Location access denied. Please enable location services or enter location manually.';
              break;
            case error.POSITION_UNAVAILABLE:
              errorMessage = 'Location information unavailable. Please enter location manually.';
              break;
            case error.TIMEOUT:
              errorMessage = 'Location request timed out. Please enter location manually.';
              break;
          }

          console.log('⚠️ No location available - user must enter manually');
          setLocationStatus('error');

          // Show a temporary error message
          setError(errorMessage);
          setTimeout(() => setError(null), 5000);
        },
        options
      );
    } else {
      console.log('⚠️ Geolocation not supported, user must enter location manually');
      setLocationStatus('error');
      setError('Geolocation not supported. Please enter your location manually.');
      setTimeout(() => setError(null), 5000);
    }
  }, [setCurrentLocation]);

  // Load initial restaurants
  useEffect(() => {
    if (currentLocation && restaurants.length === 0) {
      loadRestaurants();
    }
  }, [currentLocation, restaurants.length, loadRestaurants]);

  const handleLoadMore = useCallback(() => {
    if (!isLoading && hasMore) {
      loadRestaurants();
    }
  }, [isLoading, hasMore, loadRestaurants]);

  const handleRatingSubmit = useCallback((rating: UserRating) => {
    addUserRating(rating);
    // In production, this would also save to Firebase
    // firebaseAPI.saveUserRating(rating);
  }, [addUserRating]);

  const handleFavorite = useCallback((restaurantId: string) => {
    toggleFavorite(restaurantId);
  }, [toggleFavorite]);

  const handleRestaurantSelect = useCallback((restaurant: Restaurant) => {
    // Add the restaurant to the list if it's not already there
    if (!restaurants.find(r => r.id === restaurant.id)) {
      addRestaurants([restaurant]);
    }
    // Switch to home tab to show the restaurant
    setActiveTab('home');
  }, [restaurants, addRestaurants]);

  const renderRestaurantCard = useCallback((restaurant: Restaurant, index: number) => {
    const userRating = userRatings.find(r => r.restaurantId === restaurant.id);
    const isFavorite = favorites.includes(restaurant.id);

    return (
      <RestaurantCard
        key={restaurant.id}
        restaurant={restaurant}
        userRating={userRating}
        onRatingSubmit={handleRatingSubmit}
        onFavorite={handleFavorite}
        isFavorite={isFavorite}
        isVisible={true}
      />
    );
  }, [userRatings, favorites, handleRatingSubmit, handleFavorite]);

  const renderContent = () => {
    console.log('Rendering content. Restaurants:', restaurants.length, 'Current index:', currentIndex, 'Has more:', hasMore);

    switch (activeTab) {
      case 'home':
        return (
          <div className="h-full pt-16 pb-20">
            {restaurants.length > 0 ? (
              <InfiniteScroll
                items={restaurants}
                hasMore={hasMore}
                isLoading={isLoading}
                onLoadMore={handleLoadMore}
                renderItem={renderRestaurantCard}
                currentIndex={currentIndex}
                onIndexChange={setCurrentIndex}
              />
            ) : (
              <div className="h-full flex items-center justify-center">
                <LoadingSpinner size="large" text="Loading restaurants..." />
              </div>
            )}
          </div>
        );

      case 'create':
        return (
          <div className="h-full flex items-center justify-center bg-gray-50">
            <div className="text-center p-6">
              <div className="w-16 h-16 bg-primary-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <Plus className="w-8 h-8 text-primary-600" />
              </div>
              <h2 className="text-xl font-semibold text-gray-900 mb-2">Create Review</h2>
              <p className="text-gray-600 mb-6">Share your dining experience with the community</p>
              <button
                onClick={() => setShowCreateReview(true)}
                className="bg-primary-600 text-white px-6 py-3 rounded-lg font-semibold"
              >
                Start Review
              </button>
            </div>
          </div>
        );

      case 'search':
        return (
          <div className="h-full pb-20">
            <SearchScreen onRestaurantSelect={handleRestaurantSelect} />
          </div>
        );

      case 'profile':
        return (
          <div className="h-full pb-20">
            <ProfileScreen />
          </div>
        );

      default:
        return null;
    }
  };

  const setManualLocationHandler = useCallback(() => {
    const lat = parseFloat(manualLocation.latitude);
    const lng = parseFloat(manualLocation.longitude);

    if (isNaN(lat) || isNaN(lng)) {
      setError('Please enter valid coordinates');
      return;
    }

    if (lat < -90 || lat > 90 || lng < -180 || lng > 180) {
      setError('Invalid coordinates. Latitude: -90 to 90, Longitude: -180 to 180');
      return;
    }

    console.log('📍 Setting manual location:', { lat, lng });
    setCurrentLocation({ latitude: lat, longitude: lng });
    setLocationStatus('manual');
    setShowLocationInput(false);
    setManualLocation({ latitude: '', longitude: '' });
    clearRestaurants(); // Clear restaurants to reload with new location
  }, [manualLocation, setCurrentLocation, setError, clearRestaurants]);

  if (error) {
    return (
      <div className="h-screen-dynamic flex items-center justify-center bg-gray-100">
        <div className="text-center p-6 max-w-md">
          <div className="w-16 h-16 bg-red-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <span className="text-2xl">⚠️</span>
          </div>
          <h2 className="text-xl font-semibold text-gray-800 mb-2">Oops!</h2>
          <p className="text-gray-600 mb-6 leading-relaxed">{error}</p>
          <div className="space-y-3">
            <button
              onClick={loadRestaurants}
              className="w-full bg-blue-600 text-white px-4 py-3 rounded-lg font-semibold hover:bg-blue-700 transition-colors"
            >
              Try Again
            </button>
            <button
              onClick={() => window.location.reload()}
              className="w-full bg-gray-200 text-gray-800 px-4 py-3 rounded-lg font-semibold hover:bg-gray-300 transition-colors"
            >
              Refresh Page
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="h-screen-dynamic bg-black overflow-hidden">
      {/* Header - Only show on home tab */}
      {activeTab === 'home' && (
        <motion.header
          className="absolute top-0 left-0 right-0 z-50 p-4 flex justify-between items-center"
          initial={{ y: -100 }}
          animate={{ y: 0 }}
          transition={{ duration: 2}}
        >
          <div className="flex items-center space-x-2">
            <MapPin className="w-5 h-5 text-white" />
            <div className="flex items-center space-x-2">
              <span className="text-white text-sm font-medium">
                {locationStatus === 'loading' ? 'Getting location...' : 
                 locationStatus === 'success' ? 'Nearby' : 
                 locationStatus === 'manual' ? 'Custom location' :
                 'Set location'}
              </span>
              {locationStatus === 'success' && currentLocation && (
                <button
                  onClick={refreshLocation}
                  className="text-white/70 hover:text-white text-xs underline"
                >
                  Refresh
                </button>
              )}
              {locationStatus === 'error' && (
                <button
                  onClick={() => setShowLocationInput(true)}
                  className="text-white/70 hover:text-white text-xs underline"
                >
                  Enter location
                </button>
              )}
            </div>
          </div>

          <div className="flex items-center space-x-2">
            <button
              onClick={() => setShowSettings(true)}
              className="p-2 rounded-full bg-black/30 backdrop-blur-sm"
            >
              <Settings className="w-5 h-5 text-white" />
            </button>
          </div>
        </motion.header>
      )}

      {/* Main Content */}
      <AnimatePresence mode="wait">
        <motion.div
          key={activeTab}
          initial={{ opacity: 0, x: 20 }}
          animate={{ opacity: 1, x: 0 }}
          exit={{ opacity: 0, x: -20 }}
          transition={{ duration: 0.3 }}
          className="h-full"
        >
          {renderContent()}
        </motion.div>
      </AnimatePresence>

      {/* Bottom Navigation */}
      <BottomNavigation activeTab={activeTab} onTabChange={setActiveTab} />

      {/* Create Review Modal */}
      <AnimatePresence>
        {showCreateReview && (
          <CreateReview onClose={() => setShowCreateReview(false)} />
        )}
      </AnimatePresence>

      {/* Settings Modal */}
      <AnimatePresence>
        {showSettings && (
          <motion.div
            className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setShowSettings(false)}
          >
            <motion.div
              className="bg-white rounded-2xl p-6 w-full max-w-sm"
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.8, opacity: 0 }}
              onClick={(e) => e.stopPropagation()}
            >
              <h2 className="text-xl font-bold mb-4">Settings</h2>

              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Search Radius
                  </label>
                  <select className="w-full p-2 border border-gray-300 rounded-lg">
                    <option value="1">1 mile</option>
                    <option value="3">3 miles</option>
                    <option value="5" selected>5 miles</option>
                    <option value="10">10 miles</option>
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Price Range
                  </label>
                  <div className="space-y-2">
                    {['budget', 'moderate', 'expensive', 'luxury'].map((range) => (
                      <label key={range} className="flex items-center">
                        <input
                          type="checkbox"
                          className="mr-2"
                          defaultChecked={range !== 'luxury'}
                        />
                        <span className="capitalize">{range}</span>
                      </label>
                    ))}
                  </div>
                </div>

                <button
                  onClick={() => setShowSettings(false)}
                  className="w-full bg-primary-600 text-white py-2 rounded-lg font-semibold"
                >
                  Save Settings
                </button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Manual Location Input Modal */}
      <AnimatePresence>
        {showLocationInput && (
          <motion.div
            className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setShowLocationInput(false)}
          >
            <motion.div
              className="bg-white rounded-2xl p-6 w-full max-w-sm"
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.8, opacity: 0 }}
              onClick={(e) => e.stopPropagation()}
            >
              <h2 className="text-xl font-bold mb-4">Enter Location</h2>
              <p className="text-gray-600 mb-4">Enter your coordinates to find nearby restaurants</p>

              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Latitude
                  </label>
                  <input
                    type="number"
                    step="any"
                    placeholder="e.g., 43.4643 (Waterloo, ON)"
                    className="w-full p-2 border border-gray-300 rounded-lg"
                    value={manualLocation.latitude}
                    onChange={(e) => setManualLocation(prev => ({ ...prev, latitude: e.target.value }))}
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Longitude
                  </label>
                  <input
                    type="number"
                    step="any"
                    placeholder="e.g., -80.5204 (Waterloo, ON)"
                    className="w-full p-2 border border-gray-300 rounded-lg"
                    value={manualLocation.longitude}
                    onChange={(e) => setManualLocation(prev => ({ ...prev, longitude: e.target.value }))}
                  />
                </div>

                <div className="text-xs text-gray-500">
                  <p>💡 Quick coordinates:</p>
                  <p>• Waterloo, ON: 43.4643, -80.5204</p>
                  <p>• Toronto, ON: 43.6532, -79.3832</p>
                  <p>• Vancouver, BC: 49.2827, -123.1207</p>
                </div>

                <div className="flex space-x-3">
                  <button
                    onClick={() => setShowLocationInput(false)}
                    className="flex-1 bg-gray-200 text-gray-800 py-2 rounded-lg font-semibold"
                  >
                    Cancel
                  </button>
                  <button
                    onClick={setManualLocationHandler}
                    className="flex-1 bg-primary-600 text-white py-2 rounded-lg font-semibold"
                  >
                    Set Location
                  </button>
                </div>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

export default App;
