import React, { useState, useCallback, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Zap, Flame, Star, Clock } from 'lucide-react';
import { BoomMeterProps } from '../types';

const BoomMeter: React.FC<BoomMeterProps> = ({
  score,
  onScoreChange,
  onSpecialBoom,
  size = 'medium',
  disabled = false,
  specialBoomUsed = false,
  canUseSpecialBoom = true,
}) => {
  const [isDragging, setIsDragging] = useState(false);
  const [localScore, setLocalScore] = useState(score);
  const [showTooltip, setShowTooltip] = useState(false);
  const sliderRef = useRef<HTMLDivElement>(null);
  const isDraggingRef = useRef(false);

  const sizeClasses = {
    small: 'w-16 h-16',
    medium: 'w-20 h-20',
    large: 'w-24 h-24',
  };

  const getScoreColor = (score: number) => {
    if (score === 0) return 'text-gray-400';
    if (score <= 1) return 'text-boom-low';
    if (score <= 2) return 'text-boom-medium';
    if (score <= 3) return 'text-orange-500';
    if (score <= 4) return 'text-red-500';
    return 'text-boom-max';
  };

  const getScoreIcon = (score: number) => {
    if (score === 0) return <Star className="w-6 h-6" />;
    if (score <= 1) return <Flame className="w-6 h-6" />;
    if (score <= 2) return <Star className="w-6 h-6" />;
    if (score <= 3) return <Flame className="w-6 h-6" />;
    if (score <= 4) return <Zap className="w-6 h-6" />;
    return <Zap className="w-6 h-6" />;
  };

  const getScoreText = (score: number) => {
    if (score === 0) return 'No Rating';
    if (score === 1) return 'Meh';
    if (score === 2) return 'OK';
    if (score === 3) return 'Good';
    if (score === 4) return 'Great';
    return 'BOOM!';
  };

  const getScoreEmoji = (score: number) => {
    if (score === 0) return '🤷';
    if (score === 1) return '😐';
    if (score === 2) return '🙂';
    if (score === 3) return '😊';
    if (score === 4) return '🤩';
    return '🔥';
  };

  const handleScoreChange = useCallback((newScore: number) => {
    const clampedScore = Math.max(0, Math.min(5, newScore));
    setLocalScore(clampedScore);
    onScoreChange(clampedScore);

    // Haptic feedback on mobile
    if ('vibrate' in navigator) {
      navigator.vibrate(30);
    }
  }, [onScoreChange]);

  const calculateScoreFromPosition = useCallback((clientX: number) => {
    if (!sliderRef.current) return 0;

    const rect = sliderRef.current.getBoundingClientRect();
    const relativeX = clientX - rect.left;
    const percentage = Math.max(0, Math.min(1, relativeX / rect.width));
    const newScore = Math.round(percentage * 5);

    return newScore;
  }, []);

  const handleMouseDown = useCallback((e: React.MouseEvent) => {
    if (disabled) return;

    e.preventDefault();
    isDraggingRef.current = true;
    setIsDragging(true);
    setShowTooltip(true);

    const newScore = calculateScoreFromPosition(e.clientX);
    handleScoreChange(newScore);
  }, [disabled, calculateScoreFromPosition, handleScoreChange]);

  const handleMouseMove = useCallback((e: MouseEvent) => {
    if (!isDraggingRef.current) return;

    const newScore = calculateScoreFromPosition(e.clientX);
    handleScoreChange(newScore);
  }, [calculateScoreFromPosition, handleScoreChange]);

  const handleMouseUp = useCallback(() => {
    isDraggingRef.current = false;
    setIsDragging(false);
    setShowTooltip(false);
  }, []);

  const handleTouchStart = useCallback((e: React.TouchEvent) => {
    if (disabled) return;

    e.preventDefault();
    isDraggingRef.current = true;
    setIsDragging(true);
    setShowTooltip(true);

    const touch = e.touches[0];
    const newScore = calculateScoreFromPosition(touch.clientX);
    handleScoreChange(newScore);
  }, [disabled, calculateScoreFromPosition, handleScoreChange]);

  const handleTouchMove = useCallback((e: TouchEvent) => {
    if (!isDraggingRef.current) return;

    e.preventDefault();
    const touch = e.touches[0];
    const newScore = calculateScoreFromPosition(touch.clientX);
    handleScoreChange(newScore);
  }, [calculateScoreFromPosition, handleScoreChange]);

  const handleTouchEnd = useCallback(() => {
    isDraggingRef.current = false;
    setIsDragging(false);
    setShowTooltip(false);
  }, []);

  const handleClick = useCallback((e: React.MouseEvent) => {
    if (disabled || isDragging) return;

    const newScore = calculateScoreFromPosition(e.clientX);
    handleScoreChange(newScore);
  }, [disabled, isDragging, calculateScoreFromPosition, handleScoreChange]);

  const handleSpecialBoom = useCallback(() => {
    if (!canUseSpecialBoom || specialBoomUsed || !onSpecialBoom) return;

    onSpecialBoom();

    // Haptic feedback
    if ('vibrate' in navigator) {
      navigator.vibrate([100, 50, 100]);
    }
  }, [canUseSpecialBoom, specialBoomUsed, onSpecialBoom]);

  // Update local score when prop changes
  useEffect(() => {
    setLocalScore(score);
  }, [score]);

  // Add global event listeners
  useEffect(() => {
    if (isDragging) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
      document.addEventListener('touchmove', handleTouchMove, { passive: false });
      document.addEventListener('touchend', handleTouchEnd);

      return () => {
        document.removeEventListener('mousemove', handleMouseMove);
        document.removeEventListener('mouseup', handleMouseUp);
        document.removeEventListener('touchmove', handleTouchMove);
        document.removeEventListener('touchend', handleTouchEnd);
      };
    }
  }, [isDragging, handleMouseMove, handleMouseUp, handleTouchMove, handleTouchEnd]);

  return (
    <div className="flex flex-col items-center space-y-4">
      {/* Score Display */}
      <div className="text-center">
        <AnimatePresence mode="wait">
          <motion.div
            key={localScore}
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0.8, opacity: 0 }}
            className={`text-4xl font-bold ${getScoreColor(localScore)}`}
          >
            {getScoreEmoji(localScore)}
          </motion.div>
        </AnimatePresence>
        <motion.div
          key={`text-${localScore}`}
          initial={{ y: 10, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          className={`text-lg font-semibold ${getScoreColor(localScore)}`}
        >
          {getScoreText(localScore)}
        </motion.div>
        <div className="text-sm text-gray-600 font-mono">
          {localScore}/5 booms
        </div>
      </div>

      {/* Draggable Bar */}
      <div className="relative w-full max-w-xs">
        <motion.div
          ref={sliderRef}
          className={`relative h-12 bg-gradient-to-r from-gray-200 via-orange-200 to-red-500 rounded-full cursor-pointer select-none border-2 border-gray-300 ${
            disabled ? 'opacity-50 cursor-not-allowed' : ''
          }`}
          whileHover={!disabled ? { scale: 1.02 } : {}}
          whileTap={!disabled ? { scale: 0.98 } : {}}
          animate={isDragging ? { scale: 1.05 } : {}}
          onMouseDown={handleMouseDown}
          onTouchStart={handleTouchStart}
          onClick={handleClick}
        >
          {/* Score indicator */}
          <motion.div
            className="absolute top-1/2 transform -translate-y-1/2 w-6 h-6 bg-white rounded-full shadow-lg border-2 border-orange-500 flex items-center justify-center"
            style={{
              left: `${(localScore / 5) * 100}%`,
              transform: 'translate(-50%, -50%)',
            }}
            animate={isDragging ? { scale: 1.2 } : { scale: 1 }}
          >
            <AnimatePresence mode="wait">
              <motion.div
                key={localScore}
                initial={{ scale: 0, rotate: -180 }}
                animate={{ scale: 1, rotate: 0 }}
                exit={{ scale: 0, rotate: 180 }}
                transition={{ type: "spring", stiffness: 300, damping: 20 }}
                className={`${getScoreColor(localScore)}`}
              >
                {getScoreIcon(localScore)}
              </motion.div>
            </AnimatePresence>
          </motion.div>

          {/* Boom markers */}
          <div className="absolute inset-0 flex items-center justify-between px-2">
            {[0, 1, 2, 3, 4, 5].map((mark) => (
              <div
                key={mark}
                className={`w-1 h-4 rounded-full ${
                  mark <= localScore ? 'bg-white' : 'bg-gray-300'
                }`}
              />
            ))}
          </div>
        </motion.div>

        {/* Tooltip */}
        <AnimatePresence>
          {showTooltip && (
            <motion.div
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: 10 }}
              className="absolute -top-12 left-1/2 transform -translate-x-1/2 bg-black text-white px-3 py-1 rounded-lg text-sm whitespace-nowrap z-10"
            >
              Drag to rate
              <div className="absolute top-full left-1/2 transform -translate-x-1/2 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-black"></div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>

      {/* Special Boom Button */}
      {onSpecialBoom && (
        <div className="flex flex-col items-center space-y-2">
          <motion.button
            onClick={handleSpecialBoom}
            disabled={!canUseSpecialBoom || specialBoomUsed}
            className={`relative p-4 rounded-full text-white font-bold text-lg transition-all ${
              canUseSpecialBoom && !specialBoomUsed
                ? 'bg-gradient-to-r from-orange-500 to-red-600 hover:from-orange-600 hover:to-red-700 shadow-lg'
                : 'bg-gray-400 cursor-not-allowed'
            }`}
            whileHover={canUseSpecialBoom && !specialBoomUsed ? { scale: 1.05 } : {}}
            whileTap={canUseSpecialBoom && !specialBoomUsed ? { scale: 0.95 } : {}}
            animate={specialBoomUsed ? { scale: 0.9 } : {}}
          >
            <div className="flex items-center space-x-2">
              <Zap className="w-6 h-6" />
              <span>SPECIAL BOOM!</span>
            </div>

            {/* Used indicator */}
            {specialBoomUsed && (
              <motion.div
                initial={{ scale: 0 }}
                animate={{ scale: 1 }}
                className="absolute -top-2 -right-2 w-6 h-6 bg-green-500 rounded-full flex items-center justify-center"
              >
                <span className="text-white text-xs">✓</span>
              </motion.div>
            )}
          </motion.button>

          <div className="text-center">
            <p className="text-xs text-gray-600">
              {specialBoomUsed 
                ? 'Special boom used! 🎉' 
                : canUseSpecialBoom 
                  ? 'Once every 24 hours' 
                  : 'Available in 24 hours'
              }
            </p>
            {!canUseSpecialBoom && !specialBoomUsed && (
              <div className="flex items-center justify-center space-x-1 mt-1">
                <Clock className="w-3 h-3 text-gray-500" />
                <span className="text-xs text-gray-500">Cooldown active</span>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Instructions */}
      <div className="text-center text-xs text-gray-500">
        <p>Drag the bar to rate from 0-5 booms</p>
        <p>0 = No rating, 5 = BOOM!</p>
      </div>
    </div>
  );
};

export default BoomMeter;
