import React, { useState, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Search, MapPin, Star } from 'lucide-react';
import { Restaurant } from '../types';

interface SearchScreenProps {
  onRestaurantSelect: (restaurant: Restaurant) => void;
}

const SearchScreen: React.FC<SearchScreenProps> = ({ onRestaurantSelect }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [activeFilter, setActiveFilter] = useState('all');
  const [isSearching, setIsSearching] = useState(false);

  // Mock search results - in production, this would come from your API
  const mockSearchResults: Restaurant[] = [
    {
      id: 'search1',
      name: 'Burger Joint',
      description: 'Classic American burgers and fries',
      cuisine: 'American',
      priceRange: 'moderate',
      rating: 4.2,
      reviewCount: 89,
      address: '123 Burger St',
      city: 'San Francisco',
      state: 'CA',
      zipCode: '94102',
      phone: '(555) 123-4567',
      website: '',
      hours: {},
      images: ['https://images.unsplash.com/photo-1565299624946-b28f40a0ca4b?w=400'],
      tags: ['Burgers', 'American', 'Casual'],
      coordinates: { latitude: 37.7749, longitude: -122.4194 },
      distance: 0.3,
      isOpen: true,
      deliveryAvailable: true,
      takeoutAvailable: true,
      dineInAvailable: true,
      features: [],
      dietaryOptions: [],
      createdAt: new Date(),
      updatedAt: new Date(),
    },
    {
      id: 'search2',
      name: 'Pasta Palace',
      description: 'Authentic Italian pasta dishes',
      cuisine: 'Italian',
      priceRange: 'expensive',
      rating: 4.6,
      reviewCount: 156,
      address: '456 Pasta Ave',
      city: 'San Francisco',
      state: 'CA',
      zipCode: '94103',
      phone: '(555) 987-6543',
      website: '',
      hours: {},
      images: ['https://images.unsplash.com/photo-1513104890138-7c749659a591?w=400'],
      tags: ['Italian', 'Pasta', 'Fine Dining'],
      coordinates: { latitude: 37.7849, longitude: -122.4094 },
      distance: 0.8,
      isOpen: true,
      deliveryAvailable: false,
      takeoutAvailable: true,
      dineInAvailable: true,
      features: [],
      dietaryOptions: [],
      createdAt: new Date(),
      updatedAt: new Date(),
    },
  ];

  const handleSearch = useCallback(async () => {
    if (!searchQuery.trim()) return;

    setIsSearching(true);
    // Simulate API call
    await new Promise(resolve => setTimeout(resolve, 1000));
    setIsSearching(false);
  }, [searchQuery]);

  const filters = [
    { id: 'all', label: 'All' },
    { id: 'american', label: 'American' },
    { id: 'italian', label: 'Italian' },
    { id: 'mexican', label: 'Mexican' },
    { id: 'japanese', label: 'Japanese' },
    { id: 'chinese', label: 'Chinese' },
  ];

  return (
    <div className="h-full bg-white">
      {/* Search Header */}
      <div className="p-4 border-b border-gray-200">
        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
            placeholder="Search restaurants, cuisines, or locations..."
            className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
          />
        </div>
      </div>

      {/* Filters */}
      <div className="px-4 py-3 border-b border-gray-100">
        <div className="flex items-center space-x-2 overflow-x-auto scrollbar-hide">
          {filters.map((filter) => (
            <button
              key={filter.id}
              onClick={() => setActiveFilter(filter.id)}
              className={`px-4 py-2 rounded-full text-sm font-medium whitespace-nowrap transition-colors ${
                activeFilter === filter.id
                  ? 'bg-primary-600 text-white'
                  : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
              }`}
            >
              {filter.label}
            </button>
          ))}
        </div>
      </div>

      {/* Search Results */}
      <div className="flex-1 overflow-y-auto">
        <AnimatePresence>
          {isSearching ? (
            <motion.div
              key="loading"
              className="flex items-center justify-center py-12"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
            >
              <div className="text-center">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-500 mx-auto mb-3" />
                <p className="text-gray-500">Searching...</p>
              </div>
            </motion.div>
          ) : searchQuery ? (
            <motion.div
              key="results"
              className="p-4 space-y-4"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
            >
              {mockSearchResults.map((restaurant, index) => (
                <motion.div
                  key={restaurant.id}
                  className="bg-white rounded-xl border border-gray-200 overflow-hidden shadow-sm"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: index * 0.1 }}
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  onClick={() => onRestaurantSelect(restaurant)}
                >
                  <div className="flex">
                    <div className="w-24 h-24 flex-shrink-0">
                      <img
                        src={restaurant.images[0]}
                        alt={restaurant.name}
                        className="w-full h-full object-cover"
                      />
                    </div>
                    <div className="flex-1 p-3">
                      <h3 className="font-semibold text-gray-900 mb-1">
                        {restaurant.name}
                      </h3>
                      <p className="text-sm text-gray-600 mb-2">
                        {restaurant.description}
                      </p>
                      <div className="flex items-center justify-between">
                        <div className="flex items-center space-x-2">
                          <div className="flex items-center">
                            <Star className="w-4 h-4 text-yellow-400 fill-current" />
                            <span className="text-sm text-gray-700 ml-1">
                              {restaurant.rating}
                            </span>
                          </div>
                          <span className="text-sm text-gray-500">
                            ({restaurant.reviewCount} reviews)
                          </span>
                        </div>
                        <div className="flex items-center space-x-1">
                          <MapPin className="w-4 h-4 text-gray-400" />
                          <span className="text-sm text-gray-500">
                            {restaurant.distance} mi
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>
                </motion.div>
              ))}
            </motion.div>
          ) : (
            <motion.div
              key="empty"
              className="flex items-center justify-center py-12"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
            >
              <div className="text-center">
                <Search className="w-12 h-12 text-gray-300 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">
                  Search for restaurants
                </h3>
                <p className="text-gray-500">
                  Find your next favorite place to eat
                </p>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default SearchScreen;
