import React, { useEffect, useState, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { MapPin, Settings, Plus } from 'lucide-react';
import { useScrollEatsStore } from './store';
import { Restaurant, UserRating } from './types';
import RestaurantCard from './components/RestaurantCard';
import InfiniteScroll from './components/InfiniteScroll';
import BottomNavigation from './components/BottomNavigation';
import CreateReview from './components/CreateReview';
import SearchScreen from './components/SearchScreen';
import ProfileScreen from './components/ProfileScreen';

// Mock data for development
const mockRestaurants: Restaurant[] = [
  {
    id: '1',
    name: 'Taco Palace',
    description: 'Authentic Mexican street food with a modern twist',
    cuisine: 'Mexican',
    priceRange: 'moderate',
    rating: 4.5,
    reviewCount: 234,
    address: '123 Main St',
    city: 'San Francisco',
    state: 'CA',
    zipCode: '94102',
    phone: '(555) 123-4567',
    website: 'https://tacopalace.com',
    hours: {
      'Monday': '11:00 AM - 10:00 PM',
      'Tuesday': '11:00 AM - 10:00 PM',
      'Wednesday': '11:00 AM - 10:00 PM',
      'Thursday': '11:00 AM - 10:00 PM',
