import React, { useEffect, useState, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { MapPin, Settings, Plus } from 'lucide-react';
import { useScrollEatsStore } from './store';
import { Restaurant, UserRating } from './types';
import RestaurantCard from './components/RestaurantCard';
import InfiniteScroll from './components/InfiniteScroll';
import BottomNavigation from './components/BottomNavigation';
import CreateReview from './components/CreateReview';
import SearchScreen from './components/SearchScreen';
import ProfileScreen from './components/ProfileScreen';
import { apiService } from './services/api';

function App() {
  const {
    currentLocation,
    restaurants,
    userRatings,
    favorites,
    isLoading,
    error,
    currentIndex,
    hasMore,
    setCurrentLocation,
    addRestaurants,
    addUserRating,
    toggleFavorite,
    setLoading,
    setError,
    setHasMore,
  } = useScrollEatsStore();

  const [showSettings, setShowSettings] = useState(false);
  const [activeTab, setActiveTab] = useState('home');
  const [showCreateReview, setShowCreateReview] = useState(false);

  const loadRestaurants = useCallback(async () => {
    if (!currentLocation) return;

    setLoading(true);
    setError(null);

    try {
      // Use real Google Places API
      const realRestaurants = await apiService.getNearbyRestaurants(
        currentLocation.latitude, 
        currentLocation.longitude,
        5000 // 5km radius
      );

      if (realRestaurants.length === 0) {
        setError('No restaurants found nearby. Try expanding your search radius.');
        return;
      }

      addRestaurants(realRestaurants);
      setHasMore(false); // For now, load all at once
    } catch (err) {
      console.error('Error loading restaurants:', err);

      // Provide a more user-friendly error message
      let errorMessage = 'Failed to load restaurants. Please try again.';

      if (err instanceof Error) {
        if (err.message.includes('API key')) {
          errorMessage = 'API configuration error. Please check your Google Places API key.';
        } else if (err.message.includes('network')) {
          errorMessage = 'Network error. Please check your internet connection.';
        } else if (err.message.includes('timeout')) {
          errorMessage = 'Request timed out. Please try again.';
        } else {
          errorMessage = err.message;
        }
      }

      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  }, [currentLocation, addRestaurants, setLoading, setError, setHasMore]);

  // Get user's location on app start
  useEffect(() => {
    if (navigator.geolocation) {
      console.log('Requesting user location...');
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          console.log('Location obtained:', { latitude, longitude });
          setCurrentLocation({ latitude, longitude });
        },
        (error) => {
          console.error('Error getting location:', error);
          // Default to San Francisco
          console.log('Using default location: San Francisco');
          setCurrentLocation({ latitude: 37.7749, longitude: -122.4194 });
        }
      );
    } else {
      console.log('Geolocation not supported, using default location');
      // Default to San Francisco
      setCurrentLocation({ latitude: 37.7749, longitude: -122.4194 });
    }
  }, [setCurrentLocation]);

  // Load initial restaurants
  useEffect(() => {
    if (currentLocation && restaurants.length === 0) {
      loadRestaurants();
    }
  }, [currentLocation, restaurants.length, loadRestaurants]);

  const handleLoadMore = useCallback(() => {
    if (!isLoading && hasMore) {
      loadRestaurants();
    }
  }, [isLoading, hasMore, loadRestaurants]);

  const handleRatingSubmit = useCallback((rating: UserRating) => {
    addUserRating(rating);
    // In production, this would also save to Firebase
    // firebaseAPI.saveUserRating(rating);
  }, [addUserRating]);

  const handleFavorite = useCallback((restaurantId: string) => {
    toggleFavorite(restaurantId);
  }, [toggleFavorite]);

  const handleRestaurantSelect = useCallback((restaurant: Restaurant) => {
    // Add the restaurant to the list if it's not already there
    if (!restaurants.find(r => r.id === restaurant.id)) {
      addRestaurants([restaurant]);
    }
    // Switch to home tab to show the restaurant
    setActiveTab('home');
  }, [restaurants, addRestaurants]);

  const renderRestaurantCard = useCallback((restaurant: Restaurant, index: number) => {
    const userRating = userRatings.find(r => r.restaurantId === restaurant.id);
    const isFavorite = favorites.includes(restaurant.id);
    const isVisible = index === currentIndex;

    return (
      <RestaurantCard
        key={restaurant.id}
        restaurant={restaurant}
        userRating={userRating}
        onRatingSubmit={handleRatingSubmit}
        onFavorite={handleFavorite}
        isFavorite={isFavorite}
        isVisible={isVisible}
      />
    );
  }, [userRatings, favorites, currentIndex, handleRatingSubmit, handleFavorite]);

  const renderContent = () => {
    switch (activeTab) {
      case 'home':
        return (
          <div className="h-full pt-16 pb-20">
            {restaurants.length > 0 ? (
              <InfiniteScroll
                items={restaurants}
                hasMore={hasMore}
                isLoading={isLoading}
                onLoadMore={handleLoadMore}
                renderItem={renderRestaurantCard}
              />
            ) : (
              <div className="h-full flex items-center justify-center">
                <div className="text-center">
                  <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-500 mx-auto mb-4" />
                  <p className="text-white text-lg">Loading restaurants...</p>
                </div>
              </div>
            )}
          </div>
        );

      case 'create':
        return (
          <div className="h-full flex items-center justify-center bg-gray-50">
            <div className="text-center p-6">
              <div className="w-16 h-16 bg-primary-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <Plus className="w-8 h-8 text-primary-600" />
              </div>
              <h2 className="text-xl font-semibold text-gray-900 mb-2">Create Review</h2>
              <p className="text-gray-600 mb-6">Share your dining experience with the community</p>
              <button
                onClick={() => setShowCreateReview(true)}
                className="bg-primary-600 text-white px-6 py-3 rounded-lg font-semibold"
              >
                Start Review
              </button>
            </div>
          </div>
        );

      case 'search':
        return (
          <div className="h-full pb-20">
            <SearchScreen onRestaurantSelect={handleRestaurantSelect} />
          </div>
        );

      case 'profile':
        return (
          <div className="h-full pb-20">
            <ProfileScreen />
          </div>
        );

      default:
        return null;
    }
  };

  if (error) {
    return (
      <div className="h-screen-dynamic flex items-center justify-center bg-gray-100">
        <div className="text-center p-6">
          <h2 className="text-xl font-semibold text-gray-800 mb-2">Oops!</h2>
          <p className="text-gray-600 mb-4">{error}</p>
          <button
            onClick={loadRestaurants}
            className="bg-primary-600 text-white px-4 py-2 rounded-lg"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="h-screen-dynamic bg-black overflow-hidden">
      {/* Header - Only show on home tab */}
      {activeTab === 'home' && (
        <motion.header
          className="absolute top-0 left-0 right-0 z-50 p-4 flex justify-between items-center"
          initial={{ y: -100 }}
          animate={{ y: 0 }}
          transition={{ duration: 2}}
        >
          <div className="flex items-center space-x-2">
            <MapPin className="w-5 h-5 text-white" />
            <span className="text-white text-sm font-medium">
              {currentLocation ? 'Nearby' : 'Getting location...'}
            </span>
          </div>

          <div className="flex items-center space-x-2">
            <button
              onClick={() => setShowSettings(true)}
              className="p-2 rounded-full bg-black/30 backdrop-blur-sm"
            >
              <Settings className="w-5 h-5 text-white" />
            </button>
          </div>
        </motion.header>
      )}

      {/* Main Content */}
      <AnimatePresence mode="wait">
        <motion.div
          key={activeTab}
          initial={{ opacity: 0, x: 20 }}
          animate={{ opacity: 1, x: 0 }}
          exit={{ opacity: 0, x: -20 }}
          transition={{ duration: 0.3 }}
          className="h-full"
        >
          {renderContent()}
        </motion.div>
      </AnimatePresence>

      {/* Bottom Navigation */}
      <BottomNavigation activeTab={activeTab} onTabChange={setActiveTab} />

      {/* Create Review Modal */}
      <AnimatePresence>
        {showCreateReview && (
          <CreateReview onClose={() => setShowCreateReview(false)} />
        )}
      </AnimatePresence>

      {/* Settings Modal */}
      <AnimatePresence>
        {showSettings && (
          <motion.div
            className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setShowSettings(false)}
          >
            <motion.div
              className="bg-white rounded-2xl p-6 w-full max-w-sm"
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.8, opacity: 0 }}
              onClick={(e) => e.stopPropagation()}
            >
              <h2 className="text-xl font-bold mb-4">Settings</h2>

              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Search Radius
                  </label>
                  <select className="w-full p-2 border border-gray-300 rounded-lg">
                    <option value="1">1 mile</option>
                    <option value="3">3 miles</option>
                    <option value="5" selected>5 miles</option>
                    <option value="10">10 miles</option>
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Price Range
                  </label>
                  <div className="space-y-2">
                    {['budget', 'moderate', 'expensive', 'luxury'].map((range) => (
                      <label key={range} className="flex items-center">
                        <input
                          type="checkbox"
                          className="mr-2"
                          defaultChecked={range !== 'luxury'}
                        />
                        <span className="capitalize">{range}</span>
                      </label>
                    ))}
                  </div>
                </div>

                <button
                  onClick={() => setShowSettings(false)}
                  className="w-full bg-primary-600 text-white py-2 rounded-lg font-semibold"
                >
                  Save Settings
                </button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

export default App;
