import React, { useState, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Heart, MessageCircle, Star, MapPin, Phone, Globe } from 'lucide-react';
import { Restaurant, UserRating } from '../types';
import BoomMeter from './BoomMeter';
import { googlePlacesAPI } from '../services/api';

interface RestaurantCardProps {
  restaurant: Restaurant;
  userRating?: UserRating;
  onRatingSubmit: (rating: UserRating) => void;
  onFavorite: (restaurantId: string) => void;
  isFavorite: boolean;
  isVisible: boolean;
}

const RestaurantCard: React.FC<RestaurantCardProps> = ({
  restaurant,
  userRating,
  onRatingSubmit,
  onFavorite,
  isFavorite,
  isVisible,
}) => {
  const [showReviews, setShowReviews] = useState(false);
  const [reviews, setReviews] = useState<any[]>([]);
  const [loadingReviews, setLoadingReviews] = useState(false);
  const [boomScore, setBoomScore] = useState(userRating?.boomScore || 5);

  const handleFavorite = useCallback(() => {
    onFavorite(restaurant.id);
  }, [restaurant.id, onFavorite]);

  const handleRatingSubmit = useCallback(() => {
    const rating: UserRating = {
      id: Date.now().toString(),
      userId: 'user123', // In production, this would come from auth
      restaurantId: restaurant.id,
      boomScore,
      rating: boomScore / 2, // Convert to 5-star scale for compatibility
      review: '',
      photos: [],
      tags: [],
      isPublic: true,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    onRatingSubmit(rating);
  }, [boomScore, restaurant.id, onRatingSubmit]);

  const loadReviews = useCallback(async () => {
    if (reviews.length > 0) {
      setShowReviews(!showReviews);
      return;
    }

    setLoadingReviews(true);
    try {
      const googleReviews = await googlePlacesAPI.getPlaceReviews(restaurant.id);
      setReviews(googleReviews);
      setShowReviews(true);
    } catch (error) {
      console.error('Error loading reviews:', error);
    } finally {
      setLoadingReviews(false);
    }
  }, [restaurant.id, reviews.length, showReviews]);

  const getPriceDisplay = (priceRange: string) => {
    switch (priceRange) {
      case 'budget': return '$';
      case 'moderate': return '$$';
      case 'expensive': return '$$$';
      case 'luxury': return '$$$$';
      default: return '$$';
    }
  };

  return (
    <div className="h-full relative bg-black overflow-hidden">
      {/* Background Image */}
      <div className="absolute inset-0">
        <img
          src={restaurant.images[0] || 'https://images.unsplash.com/photo-1517248135467-4c7edcad34c4?w=800'}
          alt={restaurant.name}
          className="w-full h-full object-cover"
        />
        <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/40 to-transparent" />
      </div>

      {/* Content */}
      <div className="relative h-full flex flex-col justify-between p-6">
        {/* Header */}
        <div className="flex justify-between items-start">
          <div className="flex-1">
            <h1 className="text-3xl font-bold text-white mb-2">{restaurant.name}</h1>
            <p className="text-white/80 text-lg mb-1">{restaurant.cuisine}</p>
            <div className="flex items-center space-x-4 text-white/70">
              <div className="flex items-center">
                <Star className="w-4 h-4 text-yellow-400 fill-current mr-1" />
                <span>{restaurant.rating}</span>
                <span className="ml-1">({restaurant.reviewCount})</span>
              </div>
              <span>{getPriceDisplay(restaurant.priceRange)}</span>
              {restaurant.distance && (
                <div className="flex items-center">
                  <MapPin className="w-4 h-4 mr-1" />
                  <span>{restaurant.distance.toFixed(1)} mi</span>
                </div>
              )}
            </div>
          </div>

          <motion.button
            onClick={handleFavorite}
            className="p-3 rounded-full bg-black/30 backdrop-blur-sm"
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            <Heart 
              className={`w-6 h-6 ${isFavorite ? 'text-red-500 fill-current' : 'text-white'}`} 
            />
          </motion.button>
        </div>

        {/* Description */}
        <div className="flex-1 flex items-center justify-center">
          <div className="text-center max-w-md">
            <p className="text-white/90 text-lg mb-6">{restaurant.description}</p>

            {/* Boom Meter */}
            <div className="mb-6">
              <BoomMeter
                score={boomScore}
                onScoreChange={setBoomScore}
                size="large"
              />
            </div>

            {/* Action Buttons */}
            <div className="flex justify-center space-x-4">
              <motion.button
                onClick={handleRatingSubmit}
                className="bg-primary-600 text-white px-6 py-3 rounded-full font-semibold"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                Rate {boomScore}/10
              </motion.button>

              <motion.button
                onClick={loadReviews}
                className="bg-white/20 backdrop-blur-sm text-white px-6 py-3 rounded-full font-semibold flex items-center space-x-2"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <MessageCircle className="w-5 h-5" />
                <span>Reviews</span>
              </motion.button>
            </div>
          </div>
        </div>

        {/* Footer Info */}
        <div className="text-white/70 text-sm">
          <p className="mb-1">{restaurant.address}</p>
          <div className="flex items-center space-x-4">
            {restaurant.phone && (
              <div className="flex items-center">
                <Phone className="w-4 h-4 mr-1" />
                <span>{restaurant.phone}</span>
              </div>
            )}
            {restaurant.website && (
              <a 
                href={restaurant.website} 
                target="_blank" 
                rel="noopener noreferrer"
                className="flex items-center hover:text-white transition-colors"
              >
                <Globe className="w-4 h-4 mr-1" />
                <span>Website</span>
              </a>
            )}
          </div>
        </div>
      </div>

      {/* Reviews Modal */}
      <AnimatePresence>
        {showReviews && (
          <motion.div
            className="absolute inset-0 bg-black/90 backdrop-blur-sm z-50 flex items-center justify-center p-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setShowReviews(false)}
          >
            <motion.div
              className="bg-white rounded-2xl w-full max-w-md max-h-[80vh] overflow-y-auto"
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.8, opacity: 0 }}
              onClick={(e) => e.stopPropagation()}
            >
              <div className="p-6">
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-xl font-bold text-gray-900">Google Reviews</h2>
                  <button
                    onClick={() => setShowReviews(false)}
                    className="text-gray-500 hover:text-gray-700"
                  >
                    ✕
                  </button>
                </div>

                {loadingReviews ? (
                  <div className="text-center py-8">
                    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-500 mx-auto mb-3" />
                    <p className="text-gray-500">Loading reviews...</p>
                  </div>
                ) : reviews.length > 0 ? (
                  <div className="space-y-4">
                    {reviews.slice(0, 5).map((review, index) => (
                      <div key={index} className="border-b border-gray-200 pb-4 last:border-b-0">
                        <div className="flex items-center justify-between mb-2">
                          <div className="flex items-center space-x-2">
                            <img
                              src={review.profile_photo_url || 'https://via.placeholder.com/32'}
                              alt={review.author_name}
                              className="w-8 h-8 rounded-full"
                            />
                            <span className="font-semibold text-gray-900">{review.author_name}</span>
                          </div>
                          <div className="flex items-center">
                            <Star className="w-4 h-4 text-yellow-400 fill-current" />
                            <span className="text-sm text-gray-600 ml-1">{review.rating}</span>
                          </div>
                        </div>
                        <p className="text-gray-700 text-sm">{review.text}</p>
                        <p className="text-gray-500 text-xs mt-2">
                          {new Date(review.time * 1000).toLocaleDateString()}
                        </p>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <MessageCircle className="w-12 h-12 text-gray-300 mx-auto mb-4" />
                    <p className="text-gray-500">No reviews available</p>
                  </div>
                )}
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default RestaurantCard;
