import React, { useEffect, useState, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { MapPin, Settings, Heart, Search } from 'lucide-react';
import { useScrollEatsStore } from './store';
import { Restaurant, UserRating } from './types';
import RestaurantCard from './components/RestaurantCard';
import InfiniteScroll from './components/InfiniteScroll';
import { apiService } from './services/api';

// Mock data for development
const mockRestaurants: Restaurant[] = [
  {
    id: '1',
    name: 'Taco Palace',
    description: 'Authentic Mexican street food with a modern twist',
    cuisine: 'Mexican',
    priceRange: 'moderate',
    rating: 4.5,
    reviewCount: 234,
    address: '123 Main St',
    city: 'San Francisco',
    state: 'CA',
    zipCode: '94102',
    phone: '(555) 123-4567',
    website: 'https://tacopalace.com',
    hours: {
      'Monday': '11:00 AM - 10:00 PM',
      'Tuesday': '11:00 AM - 10:00 PM',
      'Wednesday': '11:00 AM - 10:00 PM',
      'Thursday': '11:00 AM - 10:00 PM',
      'Friday': '11:00 AM - 11:00 PM',
      'Saturday': '11:00 AM - 11:00 PM',
      'Sunday': '12:00 PM - 9:00 PM',
    },
    images: [
      'https://images.unsplash.com/photo-1565299624946-b28f40a0ca4b?w=800',
      'https://images.unsplash.com/photo-1551504734-5ee1c4a1479b?w=800',
    ],
    tags: ['Tacos', 'Mexican', 'Street Food', 'Casual'],
    coordinates: { latitude: 37.7749, longitude: -122.4194 },
    distance: 0.5,
    isOpen: true,
    deliveryAvailable: true,
    takeoutAvailable: true,
    dineInAvailable: true,
    features: ['outdoor_seating', 'live_music'],
    dietaryOptions: ['vegetarian', 'vegan'],
    createdAt: new Date(),
    updatedAt: new Date(),
  },
  {
    id: '2',
    name: 'Sushi Master',
    description: 'Premium sushi and sashimi with fresh daily catches',
    cuisine: 'Japanese',
    priceRange: 'expensive',
    rating: 4.8,
    reviewCount: 156,
    address: '456 Oak Ave',
    city: 'San Francisco',
    state: 'CA',
    zipCode: '94103',
    phone: '(555) 987-6543',
    website: 'https://sushimaster.com',
    hours: {
      'Monday': '5:00 PM - 10:00 PM',
      'Tuesday': '5:00 PM - 10:00 PM',
      'Wednesday': '5:00 PM - 10:00 PM',
      'Thursday': '5:00 PM - 10:00 PM',
      'Friday': '5:00 PM - 11:00 PM',
      'Saturday': '5:00 PM - 11:00 PM',
      'Sunday': '5:00 PM - 9:00 PM',
    },
    images: [
      'https://images.unsplash.com/photo-1579584425555-c3ce17fd4351?w=800',
      'https://images.unsplash.com/photo-1553621042-f6e147245754?w=800',
    ],
    tags: ['Sushi', 'Japanese', 'Fine Dining', 'Fresh Fish'],
    coordinates: { latitude: 37.7849, longitude: -122.4094 },
    distance: 1.2,
    isOpen: true,
    deliveryAvailable: false,
    takeoutAvailable: true,
    dineInAvailable: true,
    features: ['reservations', 'private_dining'],
    dietaryOptions: ['pescatarian'],
    createdAt: new Date(),
    updatedAt: new Date(),
  },
  {
    id: '3',
    name: 'Pizza Heaven',
    description: 'Artisanal wood-fired pizzas with premium toppings',
    cuisine: 'Italian',
    priceRange: 'moderate',
    rating: 4.3,
    reviewCount: 89,
    address: '789 Pine St',
    city: 'San Francisco',
    state: 'CA',
    zipCode: '94104',
    phone: '(555) 456-7890',
    website: 'https://pizzaheaven.com',
    hours: {
      'Monday': '11:00 AM - 11:00 PM',
      'Tuesday': '11:00 AM - 11:00 PM',
      'Wednesday': '11:00 AM - 11:00 PM',
      'Thursday': '11:00 AM - 11:00 PM',
      'Friday': '11:00 AM - 12:00 AM',
      'Saturday': '11:00 AM - 12:00 AM',
      'Sunday': '12:00 PM - 10:00 PM',
    },
    images: [
      'https://images.unsplash.com/photo-1513104890138-7c749659a591?w=800',
      'https://images.unsplash.com/photo-1565299624946-b28f40a0ca4b?w=800',
    ],
    tags: ['Pizza', 'Italian', 'Wood-fired', 'Casual'],
    coordinates: { latitude: 37.7949, longitude: -122.3994 },
    distance: 2.1,
    isOpen: true,
    deliveryAvailable: true,
    takeoutAvailable: true,
    dineInAvailable: true,
    features: ['outdoor_seating', 'live_sports'],
    dietaryOptions: ['vegetarian'],
    createdAt: new Date(),
    updatedAt: new Date(),
  },
];

function App() {
  const {
    user,
    currentLocation,
    restaurants,
    userRatings,
    favorites,
    isLoading,
    error,
    currentIndex,
    hasMore,
    setUser,
    setCurrentLocation,
    addRestaurants,
    addUserRating,
    toggleFavorite,
    setLoading,
    setError,
    setCurrentIndex,
    setHasMore,
  } = useScrollEatsStore();

  const [showSettings, setShowSettings] = useState(false);

  // Get user's location on app start
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setCurrentLocation({ latitude, longitude });
        },
        (error) => {
          console.error('Error getting location:', error);
          // Default to San Francisco
          setCurrentLocation({ latitude: 37.7749, longitude: -122.4194 });
        }
      );
    } else {
      // Default to San Francisco
      setCurrentLocation({ latitude: 37.7749, longitude: -122.4194 });
    }
  }, [setCurrentLocation]);

  // Load initial restaurants
  useEffect(() => {
    if (currentLocation && restaurants.length === 0) {
      loadRestaurants();
    }
  }, [currentLocation, restaurants.length]);

  const loadRestaurants = useCallback(async () => {
    if (!currentLocation) return;

    setLoading(true);
    setError(null);

    try {
      // For development, use mock data
      // In production, this would call: await apiService.getNearbyRestaurants(currentLocation.latitude, currentLocation.longitude);

      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 1000));

      addRestaurants(mockRestaurants);
      setHasMore(false); // No more data for demo
    } catch (err) {
      setError('Failed to load restaurants. Please try again.');
      console.error('Error loading restaurants:', err);
    } finally {
      setLoading(false);
    }
  }, [currentLocation, addRestaurants, setLoading, setError, setHasMore]);

  const handleLoadMore = useCallback(() => {
    if (!isLoading && hasMore) {
      loadRestaurants();
    }
  }, [isLoading, hasMore, loadRestaurants]);

  const handleRatingSubmit = useCallback((rating: UserRating) => {
    addUserRating(rating);
    // In production, this would also save to Firebase
    // firebaseAPI.saveUserRating(rating);
  }, [addUserRating]);

  const handleFavorite = useCallback((restaurantId: string) => {
    toggleFavorite(restaurantId);
  }, [toggleFavorite]);

  const renderRestaurantCard = useCallback((restaurant: Restaurant, index: number) => {
    const userRating = userRatings.find(r => r.restaurantId === restaurant.id);
    const isFavorite = favorites.includes(restaurant.id);
    const isVisible = index === currentIndex;

    return (
      <RestaurantCard
        key={restaurant.id}
        restaurant={restaurant}
        userRating={userRating}
        onRatingSubmit={handleRatingSubmit}
        onFavorite={handleFavorite}
        isFavorite={isFavorite}
        isVisible={isVisible}
      />
    );
  }, [userRatings, favorites, currentIndex, handleRatingSubmit, handleFavorite]);

  if (error) {
    return (
      <div className="h-screen-dynamic flex items-center justify-center bg-gray-100">
        <div className="text-center p-6">
          <h2 className="text-xl font-semibold text-gray-800 mb-2">Oops!</h2>
          <p className="text-gray-600 mb-4">{error}</p>
          <button
            onClick={loadRestaurants}
            className="bg-primary-600 text-white px-4 py-2 rounded-lg"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="h-screen-dynamic bg-black overflow-hidden">
      {/* Header */}
      <motion.header
        className="absolute top-0 left-0 right-0 z-50 p-4 flex justify-between items-center"
        initial={{ y: -100 }}
        animate={{ y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <div className="flex items-center space-x-2">
          <MapPin className="w-5 h-5 text-white" />
          <span className="text-white text-sm font-medium">
            {currentLocation ? 'Nearby' : 'Getting location...'}
          </span>
        </div>

        <div className="flex items-center space-x-2">
          <button
            onClick={() => setShowSettings(true)}
            className="p-2 rounded-full bg-black/30 backdrop-blur-sm"
          >
            <Settings className="w-5 h-5 text-white" />
          </button>
        </div>
      </motion.header>

      {/* Main Content */}
      <div className="h-full pt-16">
        {restaurants.length > 0 ? (
          <InfiniteScroll
            items={restaurants}
            hasMore={hasMore}
            isLoading={isLoading}
            onLoadMore={handleLoadMore}
            renderItem={renderRestaurantCard}
          />
        ) : (
          <div className="h-full flex items-center justify-center">
            <div className="text-center">
              <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-500 mx-auto mb-4" />
              <p className="text-white text-lg">Loading restaurants...</p>
            </div>
          </div>
        )}
      </div>

      {/* Settings Modal */}
      <AnimatePresence>
        {showSettings && (
          <motion.div
            className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setShowSettings(false)}
          >
            <motion.div
              className="bg-white rounded-2xl p-6 w-full max-w-sm"
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.8, opacity: 0 }}
              onClick={(e) => e.stopPropagation()}
            >
              <h2 className="text-xl font-bold mb-4">Settings</h2>

              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Search Radius
                  </label>
                  <select className="w-full p-2 border border-gray-300 rounded-lg">
                    <option value="1">1 mile</option>
                    <option value="3">3 miles</option>
                    <option value="5" selected>5 miles</option>
                    <option value="10">10 miles</option>
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Price Range
                  </label>
                  <div className="space-y-2">
                    {['budget', 'moderate', 'expensive', 'luxury'].map((range) => (
                      <label key={range} className="flex items-center">
                        <input
                          type="checkbox"
                          className="mr-2"
                          defaultChecked={range !== 'luxury'}
                        />
                        <span className="capitalize">{range}</span>
                      </label>
                    ))}
                  </div>
                </div>

                <button
                  onClick={() => setShowSettings(false)}
                  className="w-full bg-primary-600 text-white py-2 rounded-lg font-semibold"
                >
                  Save Settings
                </button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

export default App;
