import React, { useState, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Heart, MapPin, Clock, Star, Phone, Globe, Share2 } from 'lucide-react';
import { RestaurantCardProps } from '../types';
import BoomMeter from './BoomMeter';
import TouchGesture from './TouchGesture';

const RestaurantCard: React.FC<RestaurantCardProps> = ({
  restaurant,
  userRating,
  onRatingSubmit,
  onFavorite,
  isFavorite,
  isVisible,
}) => {
  const [showDetails, setShowDetails] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [boomScore, setBoomScore] = useState(userRating?.boomScore || 5);

  const handleBoomScoreChange = useCallback((score: number) => {
    setBoomScore(score);
  }, []);

  const handleSubmitRating = useCallback(() => {
    const rating = {
      id: userRating?.id || `rating_${Date.now()}`,
      userId: 'current-user', // This would come from auth
      restaurantId: restaurant.id,
      boomScore,
      rating: Math.ceil(boomScore / 2), // Convert 1-10 to 1-5
      review: '',
      photos: [],
      tags: [],
      isPublic: true,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    onRatingSubmit(rating);
  }, [boomScore, restaurant.id, userRating?.id, onRatingSubmit]);

  const handleFavorite = useCallback(() => {
    onFavorite(restaurant.id);
  }, [restaurant.id, onFavorite]);

  const handleShare = useCallback(() => {
    if (navigator.share) {
      navigator.share({
        title: restaurant.name,
        text: `Check out ${restaurant.name} on ScrollEats!`,
        url: window.location.href,
      });
    } else {
      // Fallback to copying to clipboard
      navigator.clipboard.writeText(`${restaurant.name} - ${restaurant.address}`);
    }
  }, [restaurant]);

  const getPriceRangeText = (priceRange: string) => {
    switch (priceRange) {
      case 'budget': return '$';
      case 'moderate': return '$$';
      case 'expensive': return '$$$';
      case 'luxury': return '$$$$';
      default: return '$$';
    }
  };

  const getRatingColor = (rating: number) => {
    if (rating >= 4.5) return 'text-green-500';
    if (rating >= 4.0) return 'text-yellow-500';
    if (rating >= 3.0) return 'text-orange-500';
    return 'text-red-500';
  };

  return (
    <TouchGesture
      onTap={() => setShowDetails(!showDetails)}
      onLongPress={handleFavorite}
    >
      <motion.div
        className="relative w-full h-screen-dynamic bg-black overflow-hidden"
        initial={{ opacity: 0 }}
        animate={{ opacity: isVisible ? 1 : 0.3 }}
        transition={{ duration: 0.3 }}
      >
        {/* Background Image */}
        <div className="absolute inset-0">
          <img
            src={restaurant.images[currentImageIndex] || '/placeholder-restaurant.jpg'}
            alt={restaurant.name}
            className="w-full h-full object-cover"
            onError={(e) => {
              e.currentTarget.src = '/placeholder-restaurant.jpg';
            }}
          />
          <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/20 to-transparent" />
        </div>

        {/* Content */}
        <div className="relative z-10 flex flex-col justify-between h-full p-6">
          {/* Header */}
          <div className="flex justify-between items-start">
            <div className="flex-1">
              <motion.h1
                className="text-3xl font-bold text-white mb-2"
                initial={{ y: 20, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ delay: 0.1 }}
              >
                {restaurant.name}
              </motion.h1>
              <motion.div
                className="flex items-center space-x-2 text-white/80"
                initial={{ y: 20, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ delay: 0.2 }}
              >
                <MapPin className="w-4 h-4" />
                <span className="text-sm">{restaurant.address}</span>
              </motion.div>
            </div>

            <motion.button
              onClick={handleFavorite}
              className="p-2 rounded-full bg-black/30 backdrop-blur-sm"
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              <Heart
                className={`w-6 h-6 ${isFavorite ? 'fill-red-500 text-red-500' : 'text-white'}`}
              />
            </motion.button>
          </div>

          {/* Middle Section */}
          <div className="flex-1 flex items-center justify-center">
            <AnimatePresence mode="wait">
              {showDetails ? (
                <motion.div
                  key="details"
                  initial={{ opacity: 0, scale: 0.8 }}
                  animate={{ opacity: 1, scale: 1 }}
                  exit={{ opacity: 0, scale: 0.8 }}
                  className="bg-white/95 backdrop-blur-sm rounded-2xl p-6 max-w-sm w-full"
                >
                  <div className="space-y-4">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-2">
                        <Star className={`w-5 h-5 ${getRatingColor(restaurant.rating)}`} />
                        <span className="font-semibold">{restaurant.rating}</span>
                        <span className="text-gray-600">({restaurant.reviewCount} reviews)</span>
                      </div>
                      <span className="text-lg font-bold text-primary-600">
                        {getPriceRangeText(restaurant.priceRange)}
                      </span>
                    </div>

                    <div className="flex items-center space-x-2 text-gray-600">
                      <Clock className="w-4 h-4" />
                      <span className="text-sm">
                        {restaurant.isOpen ? 'Open now' : 'Closed'}
                      </span>
                    </div>

                    <p className="text-gray-700 text-sm">{restaurant.description}</p>

                    <div className="flex flex-wrap gap-2">
                      {restaurant.tags.slice(0, 3).map((tag) => (
                        <span
                          key={tag}
                          className="px-2 py-1 bg-primary-100 text-primary-700 text-xs rounded-full"
                        >
                          {tag}
                        </span>
                      ))}
                    </div>

                    <div className="flex space-x-2">
                      <button className="flex-1 flex items-center justify-center space-x-2 bg-primary-600 text-white py-2 px-4 rounded-lg">
                        <Phone className="w-4 h-4" />
                        <span>Call</span>
                      </button>
                      <button className="flex-1 flex items-center justify-center space-x-2 bg-gray-600 text-white py-2 px-4 rounded-lg">
                        <Globe className="w-4 h-4" />
                        <span>Website</span>
                      </button>
                    </div>
                  </div>
                </motion.div>
              ) : (
                <motion.div
                  key="boom-meter"
                  initial={{ opacity: 0, scale: 0.8 }}
                  animate={{ opacity: 1, scale: 1 }}
                  exit={{ opacity: 0, scale: 0.8 }}
                  className="text-center"
                >
                  <BoomMeter
                    score={boomScore}
                    onScoreChange={handleBoomScoreChange}
                    size="large"
                  />
                  <motion.button
                    onClick={handleSubmitRating}
                    className="mt-4 bg-primary-600 text-white px-6 py-2 rounded-full font-semibold"
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    Rate This Place
                  </motion.button>
                </motion.div>
              )}
            </AnimatePresence>
          </div>

          {/* Footer */}
          <motion.div
            className="flex justify-between items-center"
            initial={{ y: 20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: 0.3 }}
          >
            <div className="text-white/80">
              <p className="text-sm">{restaurant.cuisine}</p>
              {restaurant.distance && (
                <p className="text-xs">{restaurant.distance.toFixed(1)} miles away</p>
              )}
            </div>

            <button
              onClick={handleShare}
              className="p-2 rounded-full bg-black/30 backdrop-blur-sm"
            >
              <Share2 className="w-5 h-5 text-white" />
            </button>
          </motion.div>
        </div>

        {/* Image Navigation Dots */}
        {restaurant.images.length > 1 && (
          <div className="absolute bottom-20 left-1/2 transform -translate-x-1/2 flex space-x-2">
            {restaurant.images.map((_, index) => (
              <button
                key={index}
                onClick={() => setCurrentImageIndex(index)}
                className={`w-2 h-2 rounded-full transition-colors ${
                  index === currentImageIndex ? 'bg-white' : 'bg-white/50'
                }`}
              />
            ))}
          </div>
        )}
      </motion.div>
    </TouchGesture>
  );
};

export default RestaurantCard;
